package com.ejeg.util;/** * @Author: Topy * @Date: 2019/10/13 14:13 */public class RRException extends RuntimeException {    private String msg;    private int code = 500;    public RRException(String msg) {        super(msg);        this.msg = msg;    }    public RRException(String msg, Throwable e) {        super(msg, e);        this.msg = msg;    }    public RRException(String msg, int code) {        super(msg);        this.msg = msg;        this.code = code;    }    public RRException(String msg, int code, Throwable e) {        super(msg, e);        this.msg = msg;        this.code = code;    }    public RRException(String message, Throwable cause, boolean enableSuppression, boolean writableStackTrace, String msg, int code) {        super(message, cause, enableSuppression, writableStackTrace);        this.msg = msg;        this.code = code;    }    public String getMsg() {        return msg;    }    public void setMsg(String msg) {        this.msg = msg;    }    public int getCode() {        return code;    }    public void setCode(int code) {        this.code = code;    }}